.Widgets.fullscreen {
  overflow: clip;
}

/* Glassy Effect :3 */
.LiteratureClock, .Links {
  backdrop-filter: blur(10px);
  background: #ffffff15;
  border-radius: 1rem;
  outline: solid #ffffff20 0.2rem;
  padding: 0.5rem; /* Only for .LiteratureClock */
}

.LiteratureClock blockquote {
  margin: 1.5rem;
}

.LiteratureClock cite {
  margin-bottom: 1rem;
  margin-left: 2rem;
}

/* Vertical Links translateY(20%) */
.middleRight .Widget {
  transform: rotate(-90deg);
  transform-origin: center right;
  margin-bottom: 99% !important;
  padding: 5rem 2rem;
  padding-bottom: 0;

  transition: transform cubic-bezier(.18,.89,.32,1.5) 0.5s, padding 0.5s;
  will-change: padding, transform;

  &:is(:hover, :focus-within) {
    transform: rotate(-90deg) translateY(-20%);
    padding: 2rem;
    padding-bottom: 0;
  }
}

.Links {
  display: flex;
  padding: 0.8rem 1rem;
  padding-bottom: 2rem;
  border-bottom-left-radius: 0;
  border-bottom-right-radius: 0;
}

/* Hide Credit Unless Hovered */
.credit {
  transform: translateY(6rem); /* Fix position */
  user-select: none;
}

.credit a:is(:hover, :focus-within) {
  transition: color 0.5s;
  color: #fff;
}

.photo {
  opacity: 0;
  width: max-content;
  padding: 3rem;
  transform: translateY(-3rem) translateX(-3rem); /* Fix position */
  transition: opacity 0.5s;

  &:is(:hover, :focus-within) {
    opacity: 1;
  }
}

/* Disable pointer events on weather widget */
.Weather {
  pointer-events: none;
  user-select: none;
}

/* Dim Some Things */
.Dashboard .Overlay,
.Dashboard .Background .credit {
  color: #fffa;
}

/* Animate In The Settings */
@keyframes slide-in {
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  60% {
    opacity: 1;
  }
}

.Settings .plane {
  animation: slide-in 1s;
}

/* Dark Mode */
.Settings .plane {
  background-color: #1e1e1e;
  color: #b3b4b6;
}
.Settings .Widget {
  background: #262626;
}
.Settings input[type=email].primary,
.Settings input[type=file].primary,
.Settings input[type=number].primary,
.Settings input[type=text].primary,
.Settings input[type=time].primary,
.Settings input[type=url].primary,
.Settings input[type=color],
.Settings textarea.primary,
.Settings select.primary,
.Settings select {
  background-color: #262626;
  border: 1px solid #bdc3c7;
  border-radius: 3px;
}
.Settings h1,
.Settings h2,
.Settings h3,
.Settings h4,
.Settings h5 {
  color: white !important;
}
.Settings textarea,
.Settings input {
  background: #262626;
  color: #c3c4c6!important;
  outline: 0;
}
.Settings hr {
  border: revert !important;
}

/* Dark Mode Buttons */
.Settings button.button--icon {
  background-color: #262626 !important;
  border: 1px solid #bdc3c7;
}
.Settings button.button--icon svg {
  transform: translateY(-1px) translateX(-1px);
  color: white !important;
}

/* Dark Mode Sliders */
.Settings ::-moz-range-track {
  border: 1px solid #bdc3c7;
  padding: 1px;
  border-radius: 999px
}

.Settings ::-moz-range-thumb {
  background: white;
  border: none;
  padding: 2px;
  border-radius: 999px;
}

.Settings input[type=range] + datalist {
  display: flex;
  margin-top: -2px;
  width: 167px;
  justify-content: space-between;
  height: 5px;
  overflow: hidden;
  padding:0 8px;
}

.Settings input[type=range] + datalist option {
  width: 1px;
  background-color: #bdc3c7;
  color: transparent;
}
